import React from "react";
import { PiCaretDownLight } from "react-icons/pi";

const Header = () => {
  return (
    <nav className="bg-gray-800 text-purple p-4">
      <div className="flex justify-between items-center">
        <div className="flex space-x-4 items-center">
          <img
            src="https://www.radiustheme.com/demo/wordpress/themes/quiklearn/wp-content/uploads/2023/08/logo-dark.svg"
            alt=""
          ></img>
          <div className="flex space-x-4">
            <DropdownMenu title="Home" items={["Home1", "Home2", "Home3"]} />
            <DropdownMenu
              title="Courses"
              items={[
                "Course Default",
                "Course style 2",
                "Course style 3",
                "Course style 4",
              ]}
            />
            {/* Diğer menüler */}
          </div>
        </div>
      </div>
    </nav>
  );
};

const DropdownMenu = ({ title, items }) => {
  return (
    <div className="dropdown relative group">
      <div className="relative">
        <button className="dropbtn text-lg flex items-center">
          {title} <PiCaretDownLight className="ml-2" />
        </button>
        <div className="dropdown-content absolute hidden bg-white p-4 shadow-md mt-1 group-hover:block">
          <div className="row flex">
            <div className="column w-1/3 p-4">
              {items.map((item, index) => (
                <a key={index} href="/" className="block text-md py-2">
                  {item}
                </a>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
