import React, { useState } from "react";
import { PiCaretDownLight } from "react-icons/pi";

const Header = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const handleDropdownClick = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleMouseLeave = () => {
    // Eğer mouse dropdown içeriğinden çıkarsa kapatma
    // Eğer mouse buttona tıklandıysa kapatma (bunu bir önceki tıklama ile kontrol et)
    if (!isButtonClicked) {
      setDropdownOpen(false);
    }
  };

  const handleButtonClick = () => {
    // Dropdown butonuna tıklandığında bu durumu sakla
    setButtonClicked(true);
  };

  const [isButtonClicked, setButtonClicked] = useState(false);

  return (
    <nav className="bg-gray-800 text-purple p-4">
      <div className="flex justify-between items-center">
        <div className="flex space-x-4">
          <a href="#home" className="text-lg">
            Home
          </a>
          <a href="#news" className="text-lg">
            News
          </a>
        </div>
        <div
          className="dropdown relative group"
          onMouseLeave={handleMouseLeave}
        >
          <div className="relative">
            <button
              className="dropbtn text-lg flex items-center"
              onClick={handleButtonClick}
            >
              Dropdown <PiCaretDownLight className="ml-2" />
            </button>
          </div>

          <div
            className={`dropdown-content absolute ${
              isDropdownOpen ? "block" : "hidden"
            } bg-white p-4 shadow-md mt-1`}
          >
            <div className="header bg-red-500 p-4 text-white">
              <h2>Mega Menu</h2>
            </div>
            <div className="row flex">
              <div className="column w-1/3 p-4">
                <h3 className="text-lg">Category 1</h3>
                <a href="#" className="block text-md py-2">
                  Link 1
                </a>
                <a href="#" className="block text-md py-2">
                  Link 2
                </a>
                <a href="#" className="block text-md py-2">
                  Link 3
                </a>
              </div>
              <div className="column w-1/3 p-4">
                <h3 className="text-lg">Category 2</h3>
                <a href="#" className="block text-md py-2">
                  Link 1
                </a>
                <a href="#" className="block text-md py-2">
                  Link 2
                </a>
                <a href="#" className="block text-md py-2">
                  Link 3
                </a>
              </div>
              <div className="column w-1/3 p-4">
                <h3 className="text-lg">Category 3</h3>
                <a href="#" className="block text-md py-2">
                  Link 1
                </a>
                <a href="#" className="block text-md py-2">
                  Link 2
                </a>
                <a href="#" className="block text-md py-2">
                  Link 3
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
