import React, { useEffect, useRef } from "react";
import ParallaxLayer from "./ParallaxLayer";

const Parallax = () => {
  const xImgRef = useRef(null);
  const mainImgRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const layers = [
        { speed: 1, position: xImgRef },
        { speed: 0.5, position: mainImgRef },
        // Diğer resimler için aynı şekilde ekleyebilirsiniz.
      ];

      layers.forEach(({ speed, position }) => {
        // Ref'in mevcut durumu null ise işlem yapma
        if (position.current) {
          const x = (window.innerWidth - e.pageX * speed) / 100;
          const y = (window.innerHeight - e.pageY * speed) / 100;

          position.current.style.transform = `translate(${x}px, ${y}px)`;
        }
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <div className="bg-gray grid grid-cols-2">
      {/* Metin içerikleri burada */}
      <div className="col-span-1 relative ">
        <ParallaxLayer
          imageUrl="https://www.radiustheme.com/demo/wordpress/themes/quiklearn/wp-content/uploads/2023/08/hero-shape-10.png"
          speed={1}
          position={xImgRef}
        />
        {/* Diğer resimler için aynı şekilde ParallaxLayer bileşenini ekleyin */}
      </div>
    </div>
  );
};

export default Parallax;
